import jwt from "jsonwebtoken";
import dotenv from "dotenv";

dotenv.config();

const verifyToken = (req, res, next) => {
    try {
        const token = req.header('x-auth-token');
        if (!token) return res.status(401).send({ status: 401, Error: "unauthorized access" });
        const authorizedUser = jwt.verify(token, process.env.SECRET_KEY);
        req.authorizedUser = authorizedUser;
        next();
    } catch (error) {
        return res.status(403).send({ status: 403, Error: "Token invalid or expired" });
    }
}

export default verifyToken;

import Joi from 'joi';

export function courseValidate(req, res, next) {
    const courseValiation = Joi.object({
        name: Joi.string().min(4).required(),
        content: Joi.string().min(50).max(10000).required(),
        genre: Joi.string().min(10).max(50).required(),
        lecturerId: Joi.string()

    });
    const result = courseValiation.validate(req.body);
    if (result.error) return res.status(400).json({ Message: result.error.details[0].message });
    next();
}
