const express = require('express');
const router = express.Router();
const userController = require('../controllers/users');
const passport = require('passport');
require("../config/passport-setup");

router.get('/', function(req, res, next) {
    res.send('respond with a resource');
});

router.get('/google', passport.authenticate('google', { scope: ['profile', 'email'] }));
router.get('/login', passport.authenticate('google', { failureRedirect: '/failed' }), userController.login);

module.exports = router;

const users = require('../models').Users;
const { assignToken } = require("../helpers/assignToken");

const login = async (req, res) => {
    try {
        const { email } = req.user._json;
        const user = await users.findOne({ where: { email: email } });
        if (!user) { return res.status(404).json({ error: "user is not registered!" }) }
        const token = assignToken({ userName: user.name, email: user.email });
        return res.status(200).json({ message: "successfully logged in!", token });
    } catch (error) {
        return res.status(500).json({ error: error.message })
    }
}

module.exports = {
    login
}

import jwt from "jsonwebtoken";
import dotenv from "dotenv";

dotenv.config();

export const assignToken = (data) => jwt.sign(data, process.env.SECRET_KEY, {
    expiresIn: "1d"
});

const passport = require('passport');
const GoogleStrategy = require('passport-google-oauth20').Strategy;
require("dotenv").config();

passport.serializeUser(function (user, done) {
    done(null, user);
});

passport.deserializeUser(function (user, done) {
    done(null, user);
});

passport.use(new GoogleStrategy({
    clientID: process.env.CLIENT_ID,
    clientSecret: process.env.CLIENT_SECRET,
    callbackURL: "http://localhost:3000/users/login"
},
    function (accessToken, refreshToken, profile, done) {
        return done(null, profile);
    }
));
